INSERT INTO wp_postmeta VALUES("113","79","costruzione","<p><span style=\"color: #555555;\"><span style=\"font-family: Georgia, \'Times New Roman\', \'Bitstream Charter\', Times, serif;\"><span style=\"font-size: small;\">Ho usato tavole di abete lamellare, piuttosto che MDF, perchè non avevo voglia di preoccuparmi della verniciatura e del colore da dare. In realtà, una pennellata, anche se di venice incolore, l\'ho data lo stesso, perchè è necessario un minimo di protezione per il legno, altrimenti è eccessivamente soggetto allo sporco e ai graffi. Inoltre, l\'abete lamellare non facilissimo da lavorare, a differenza dell\'MDF, quindi la mia scelta non è stata ottima.</span></span></span></p><p>&nbsp;</p><p><span style=\"color: #555555;\"><span style=\"font-family: Georgia, \'Times New Roman\', \'Bitstream Charter\', Times, serif;\"><span style=\"font-size: small;\">Appena ho cercato di forare </span></span></span></p>");
